import {useTaskList} from '../AppContext/AppProvider'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()

    var teste = localStorage.getItem('task')

    return(
        <div className="taskList">
            
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {
                        
            function delTask(){
                var id = task.id
                const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }
            function checkBox (){
                if(task.done === false){
                    task.done = true
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                } else{
                    task.done = false
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                }
            }
            return (
                
                    <div key={task.id}>
                        <p>{teste}</p>
                        <input 
                            id='checkbox'
                            type="checkbox"
                            checked={task.done}
                            onClick={checkBox}>
                        </input>
                        <label>{task.name}</label>
                        <button onClick={checkBox}>X</button>               
                    </div>

                    )
            })}
        </div>
    )
}
