import { useTaskList } from '../AppContext/AppProvider'
import { useState } from 'react'
import liststyle from './liststyle.css'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()
    const [ getUpdate, setUpdate ] = useState('')

    return(
        <div className="taskList">
            
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {

            /// Função de deletetar tarefa     
            function delTask(){
                var id = task.id
                const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }

            /// Função de atualizar o status da variavel "done" da tarefa
            function checkBox(){
                if(task.done === false){
                    task.done = true
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                } else{
                    task.done = false
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                }
            }

            function updateTask(){
                    task.name = getUpdate
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                }

            return (
                    <div className='tasklist' key={task.id}>
                        <input 
                            className='checkbox'
                            type="checkbox"
                            checked={task.done}
                            onChange={checkBox}>
                        </input>
                        
                        <form onSubmit={updateTask}>
                            <input type='text' placeholder={task.name} style={{alignItems: 'left',visibility: task.update ? 'visible' : 'hidden' }} required value={getUpdate} onChange={(e) => setUpdate(e.target.value)} className='tasktext' maxLength={50} />
                        </form>
                        <button className='editbutton' onClick={updateTask}>✏️</button>   
                        <button className='deletebutton' onClick={delTask}>❌</button>               
                    </div>
                    )})}
        </div>
    )
}
