import { useEffect, useRef } from "react";
import { useText, useTaskList } from "../AppContext/AppProvider";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import addstyle from "./addstyle.css";

export const AddTask = () => {
  const { getText, setText } = useText();
  const { getTaskList, setTaskList } = useTaskList();
  const target = useRef("");

  const notify = () => toast("Wow so easy !");

    /// Função de envio de formulario
  const createTask = (e) => {
    e.preventDefault();

    var checkText = target.current.value.toString().indexOf(" ");

    if (
      checkText !== 0 &&
      target.current.value !== "" &&
      target.current.value !== null
    ) {
      /// Criar objeto "tarefa"
      let newtask = {
        id: new Date().getTime().toString(),
        name: target.current.value,
        done: false,
      };
      /// Salvar objeto "tarefa" no array
      setTaskList([...getTaskList, newtask]);
      setText("");
      target.current.value = "";
    } else {
      setText("");
      target.current.value = "";
      toast.error('Não é possivel criar tarefas vazias ou iniciadas com um espaço!',  {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
        if (checkText !== 0) {
          /// Criar objeto "tarefa"
          let newtask = {
            id: new Date().getTime().toString(),
            name: target.current.value,
            done: false,
          };
          /// Salvar objeto "tarefa" no array
          setTaskList([...getTaskList, newtask]);
          setText("");
          target.current.value = "";
        }
    }
  };

  /// Salvar no localstorage
  useEffect(() => {
    localStorage.setItem("task", JSON.stringify(getTaskList));
  }, [getTaskList]);

  return (
    /// Inicio do formulario
    <form autoComplete="off" onSubmit={createTask} className="addtask">
      <input
        type="text"
        placeholder="Adicionar Tarefa"
        required
        ref={target}
        className="addtext"
        maxLength={45}
      />
      <button type="submit" className="addbutton" onClick={createTask}>
        ➕
      </button>
      <ToastContainer />
    </form>
    
    /// Fim do formulario
  );
};
