import { AddTask } from '../AddTask/AddTask'

export const ListTask = ({getLocalStorage}) =>{

    console.log(getLocalStorage)

    useEffect(()=>{
        localStorage.getItem('task')
    },[getLocalStorage])

    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
                {{tasklist}.map((individualTask,index) => (
                    <div className='renderTask' key={individualTask.id}>
                        <input type='checkbox'/>
                        <span>{individualTask.name}</span>
                    </div>
                ))}
        </div>
    )
}
