import { useTaskList } from "../AppContext/AppProvider"
import { Modal, Button, Alert } from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.min.css"
import { useState, useRef } from "react"
import liststyle from "./liststyle.css"

export const ListTask = () => {
  const { getTaskList, setTaskList } = useTaskList()

  return (
    <div>
      {getTaskList.map((task) => {

        function delTask() {
          var id = task.id
          const fill = getTaskList.filter((task) => {
            return task.id !== id
          })
          setTaskList(fill)
        }

        function checkBox() {
          if (task.done) {
            task.done = false
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          } else {
            task.done = true
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          }
        }

          function UpdateTask() {
            const [showModal, setShowModal] = useState(false);
            const handleClose = () => setShowModal(false);
            const handleShow = () => setShowModal(true);
            const [show, setShow] = useState(false);
            const updateText = useRef();

            var value = "abc."

              function checkUpdate() {

              if(updateText.current.value !== '' && updateText.current.value !== '.' && updateText.current.value !== null)
              {
                  task.name = updateText.current.value
                  const boxUpdate = getTaskList.filter(() => {
                    return getTaskList
                  })
                  setTaskList(boxUpdate)
                  setShowModal(false)
                  console.log(value.indexOf("."))
              }else{
                  setShow(true)
                  console.log(value.indexOf("."))
              }
            }  
          
            return (
              <div>
                <button className="editbutton" onClick={handleShow}>
                ✏️
                </button>
          
                <Modal show={showModal} onHide={handleClose}>

                  <Modal.Header>
                    <Modal.Title>Editar Tarefa</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <input placeholder={task.name} ref={updateText} className="taskedit"></input>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                      Fechar
                    </Button>
                    <Button variant="primary" onClick={checkUpdate}>
                      Salvar
                    </Button>
                    
                  </Modal.Footer>
                  <Alert show={show} variant="danger" onClose={() => setShow(false)} dismissible>
                     <Alert.Heading>Erro</Alert.Heading>
                     <p>A tarefa não pode estar vazia.</p>
                    </Alert>
                </Modal>
              </div>
            )
          }

          return (
            <div className="tasklist" key={task.id}>
              <input
                className="checkbox"
                type="checkbox"
                checked={task.done}
                onChange={checkBox}
              ></input>
              <label className="tasktext">{task.name}</label>
              <UpdateTask></UpdateTask>
              <button className="deletebutton" onClick={delTask}>
                ❌
              </button>
            </div>
          )
      })}
    </div>
  )
}
