import { useEffect,useState, useRef }  from 'react'
import { useText, useTaskList } from '../AppContext/AppProvider'
import { Overlay, Tooltip, Button } from 'react-bootstrap'
import addstyle from './addstyle.css'

export const AddTask = () =>{
    const { getText, setText} = useText()
    const { getTaskList, setTaskList } = useTaskList()

    /// Função de envio de formulario

    function Example() {
        const [show, setShow] = useState(false);
        const target = useRef(null);
      
        return (
          <>
            <form autoComplete="off" onSubmit={createTask} className="addtask">
                <input type='text' placeholder="Adicionar Tarefa" required onChange={(e) => setText(e.target.value)} value={getText} ref={target} className='addtext' maxLength={50} />
                <button type="submit" className='addbutton'>
                ➕
                </button>

            <button type="submit" className='addbutton' onClick={() => setShow(!show)}>
            ➕
            </button>
            </form>
            <Overlay target={target.current} show={show} placement="left">
              {(props) => (
                <Tooltip id="overlay-example" {...props}>
                  My Tooltip
                </Tooltip>
              )}
            </Overlay>
          </>
        );
      }



    const createTask = (e) =>{
        e.preventDefault()

        var checkText = getText.toString().indexOf(" ")
        if(checkText !== 0){
        /// Criar objeto "tarefa"
        let newtask = {
            id: new Date().getTime().toString(),
            name: getText,
            done: false
        }
        /// Salvar objeto "tarefa" no array
        setTaskList([...getTaskList, newtask])
        setText('')
        } else{
            setText('')
        }
    }


    /// Salvar no localstorage 
    useEffect(()=>{
        localStorage.setItem('task', JSON.stringify(getTaskList))
    },[getTaskList])

    return(
        /// Inicio do formulario     
 
                <Example></Example>

        /// Fim do formulario
    )
}
