import { useState, useEffect }  from 'react'

export const AddTask = ({getLocalStorage}) =>{

    /// Definir state
    const [getText, setText] = useState('')

    /// Lista de array
    const [getTaskList, setTaskList] = useState(getLocalStorage())
    console.log(getTaskList)

    /// Salvar no localstorage
    useEffect(()=>{
        localStorage.setItem('task', JSON.stringify(getTaskList))
    },[getTaskList])

    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
                {getLocalStorage.map((individualTask,index) => (
                    <div className='renderTask' key={individualTask.id}>
                        <input type='checkbox'/>
                        <span>{individualTask.name}</span>
                    </div>
                ))}
        </div>

    )
}
