import {useTaskList} from '../AppContext/AppProvider'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()

    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {

   
const { getTaskList, setTaskList} = useTaskList()
            function checkBox (){
                if(task.done === false){
                    task.done = true
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                } else{
                    task.done = false
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                }
                
            }
                        
            function delTask(){
                var id = task.id
                    const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }

            return (
                
                    <div key={task.id}>
                        <input 
                            type="checkbox"
                            checked={task.done}
                            onClick={checkBox}>
                        </input>
                        <label>{task.name}</label>
                        <button onClick={delTask}>X</button>               
                    </div>

                    )
            })}
        </div>
    )
}
