import { useEffect, useRef } from "react";
import { useText, useTaskList } from "../AppContext/AppProvider";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import addstyle from "./addstyle.css";

export const AddTask = () => {
  const { getText, setText } = useText();
  const { getTaskList, setTaskList } = useTaskList();
  const target = useRef("");

  /// Função de envio de formulario
  const createTask = (e) => {
    e.preventDefault();

    var name = target.current.value;
    while (name.charAt(0) === " ") {
      name = name.substring(1);
      console.log(name);
    }

    var checkText = name.toString().indexOf(" ");
    if (checkText !== 0 && name !== null && name !== "") {
      let newtask = {
        id: new Date().getTime().toString(),
        name: name,
        done: false,
      };

      /// Salvar objeto "tarefa" no array
      setTaskList([...getTaskList, newtask]);
      setText("");
      /// Salva objeto "tarefa" no array
      target.current.value = "";
    } else {
      toast.error("Não é possivel criar uma tarefa vazia!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        theme: "colored",
        progress: undefined,
      });

      target.current.value = "";
    }
  };

  /// Salvar no localstorage
  useEffect(() => {
    localStorage.setItem("task", JSON.stringify(getTaskList));
  }, [getTaskList]);

  return (
    /// Inicio do formulario
    <form autoComplete="off" onSubmit={createTask} className="addtask">
      <input
        type="text"
        placeholder="Adicionar Tarefa"
        required
        ref={target}
        className="addtext"
        maxLength={45}
      />
      <button type="submit" className="addbutton" onClick={createTask}>
        ➕
      </button>
      <ToastContainer />
    </form>

    /// Fim do formulario
  );
};
