import { useTaskList } from "../AppContext/AppProvider"
import { useRef, useMemo, useCallback, useState } from "react"
import liststyle from "./liststyle.css"

export const ListTask = () => {
  const { getTaskList, setTaskList } = useTaskList()
  const updatateText = useRef("a")
  const [getUpdate, setUpdate] = useState(false)

  return (
    <div>
      {getTaskList.map((task) => {

        task.update = getUpdate

        function delTask() {
          var id = task.id
          const fill = getTaskList.filter((task) => {
            return task.id !== id
          })
          setTaskList(fill)
        }

        function checkBox() {
          if (task.done) {
            task.done = false
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          } else {
            task.done = true
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          }
        }

        function updateTask() {
          if (task.update) {
            task.update = false
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          } else {
            task.name = updatateText.current.value
            task.update = true
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
          }
        }

        function TaskListRender() {
          if (task.update) {
            return (
              <div className="tasklist" key={task.id}>
                <input
                  className="checkbox"
                  type="checkbox"
                  checked={task.done}
                  onChange={checkBox}
                ></input>
                <label className="tasktext">{task.name}</label>
                <button className="editbutton" onClick={updateTask}>
                  ✏️
                </button>
                <button className="deletebutton" onClick={delTask}>
                  ❌
                </button>
              </div>
            )
          } else {
            return (
              <div key={task.id}>
                <form className="tasklist" onSubmit={updateTask}>
                  <input
                    type="text"
                    placeholder={task.name}
                    defaultValue={task.name}
                    className="taskedit"
                    ref={updatateText}
                    maxLength={50}
                    required
                  />
                  <button className="editbutton" type="submit">
                    ✏️
                  </button>
                  <button className="deletebutton" onClick={delTask}>
                    ❌
                  </button>
                </form>
              </div>
            )
          }
        }
        return <TaskListRender key={task.id} />
      })}
    </div>
  )
}
