import {useTaskList} from '../AppContext/AppProvider'
import { useEffect } from 'react'
import liststyle from './liststyle.css'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()

    useEffect(()=>{
        localStorage.setItem('task', JSON.stringify(getTaskList))
    },[getTaskList])

    return(
        <div className="taskList">
            
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {
                        
            function delTask(){
                var id = task.id
                const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }

            function checkBox (){
                if(task.done === false){
                    task.done = true
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                } else{
                    task.done = false
                    console.log(getTaskList)
                    setTaskList(getTaskList)
                }
            }

            return (
                
                    <div className='task' key={task.id}>
                        <input 
                            id='checkbox'
                            type="checkbox"
                            checked={task.done}
                            onClick={checkBox}>
                        </input>
                        <label c>{task.name}</label>
                        <button className='deleteButton' onClick={delTask}>X</button>               
                    </div>

                    )
            })}
        </div>
    )
}
