import {useTaskList} from '../AppContext/AppProvider'

export const ListTask = ({getLocalStorage}) =>{

    const { getTaskList, setTaskList} = useTaskList()

    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((event) => {

            const { id, name, done } = event

 return (
        <div key={id}>
            <input 
                type="checkbox"
                checked={done}
                onClick={(event) => done != done}
            >
            </input>
            <label>{name}</label>
            <button className="deleteButton" >X</button>               
        </div>

        )
})}
        </div>
    )
}
