import { useState, useEffect, useContext }  from 'react'
import AppContext from '../AppContext/AppContext'

export const AddTask = ({getLocalStorage}) =>{

    const { tasklist } = useContext(AppContext)

    /// Definir state
    const [getText, setText] = useState('')

    /// Lista de array
    const [getTaskList, setTaskList] = useState(getLocalStorage())
    console.log(getTaskList)

    /// Função de envio de formulario
    const createTask = (e) =>{
        e.preventDefault()

        /// Criar objeto "tarefa"
        let newtask = {
            id: new Date().getTime().toString(),
            name: getText,
            done: false
        }

        setTaskList([...getTaskList, newtask])
        setText('')
    }

    /// Salvar no localstorage
    useEffect(()=>{
        localStorage.setItem('task', JSON.stringify(getTaskList))
    },[getTaskList])

    return(
        /// Inicio do formulario
        
        <div className="form">
            <AppContext.Consumer>
                <p>{tasklist}</p>
            <form autoComplete="off" onSubmit={createTask}>
                <input type='text' placeholder="Adicionar Tarefa" required onChange={(e) => setText(e.target.value)} value={getText} />
                <button type="submit">
                      +
                </button>
            </form>
            </AppContext.Consumer>
        </div>
        /// Fim do formulario
    )
}
