import { useTaskList } from '../AppContext/AppProvider'
import { useState } from 'react'
import liststyle from './liststyle.css'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()
    const [ getUpdate, setUpdate ] = useState('')

    return(
        <div className="taskList">
            
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {

            /// Função de deletetar tarefa     
            function delTask(){
                var id = task.id
                const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }

            /// Função de atualizar o status da variavel "done" da tarefa
            function checkBox(){
                if(task.done){
                    task.done = false
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                } else{
                    task.done = true
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                }
            }

            function updateTask(){
                if(task.update){
                    task.update = false
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
                }else{
                    task.update = true
                    const boxUpdate = getTaskList.filter(() =>{
                        return getTaskList
                    })
                    setTaskList(boxUpdate)
            }
                    
                }
                  
                function TaskListRender() {
                    const update = task.update

                    if (update){
                        return([
                            <div className='tasklist' key={task.id}>  
                                <input 
                                    className='checkbox'
                                    type="checkbox"
                                    checked={task.done}
                                    onChange={checkBox}>
                                </input>,
                                <label className='tasktext'>{task.name}</label>
                            </div>
                        ])
                    }else{
                        return ([
                            <div className='tasklist' key={task.id}>    
                                <input type='text' placeholder={task.name} required className='tasktext' maxLength={50} />,
                                <button className='editbutton' onClick={updateTask}>✏️</button>,
                                 <button className='deletebutton' onClick={delTask}>❌</button>  
                            </div>

                        ])
                    }
                }

            return (       
                        <TaskListRender />
                    )})}
        </div>
    )
}
