import { useState, useEffect }  from 'react'

export const ListTask = ({getLocalStorage}) =>{

    /// Lista de array
    const list = getLocalStorage


    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
                {getLocalStorage}.map((individualTask,index) => (
                    <div className='renderTask' key={individualTask.id}>
                        <input type='checkbox'/>
                        <span>{individualTask.name}</span>
                    </div>
                ))}
        </div>
    )
}
