import { useState, useEffect }  from 'react'
import { AddTask } from '../AddTask/AddTask'

export const ListTask = ({getTaskList}) =>{

    console.log(getTaskList)

    var list = 
    useEffect(()=>{
        var list = localStorage.setItem('task', JSON.stringify(getTaskList))
    },[getTaskList])

    return(
        <div className="taskList">
            {/* Renderizar a lista de tarefas */}
                {list.map((individualTask,index) => (
                    <div className='renderTask' key={individualTask.id}>
                        <input type='checkbox'/>
                        <span>{individualTask.name}</span>
                    </div>
                ))}
        </div>
    )
}
