import {useTaskList} from '../AppContext/AppProvider'

export const ListTask = () =>{
    
    const { getTaskList, setTaskList} = useTaskList()

    var teste = localStorage.getItem('task')
    return(
        <div className="taskList">
            
            {/* Renderizar a lista de tarefas */}
            {getTaskList.map((task) => {

                        
            function delTask(){
                var id = task.id
                    const fill = getTaskList.filter((task) =>{
                    return task.id !== id
                })
                setTaskList(fill)
            }

            return (
                
                    <div key={task.id}>
                        <p>{teste}</p>
                        <input 
                            id='checkbox'
                            type="checkbox"
                            checked={task.done}
                            onClick={function checkBox (){
                                if(task.done === false){
                                    task.done = true
                                    var update = getTaskList
                                    console.log(getTaskList)
                                    return setTaskList(getTaskList)
                                    document.getElementById("checkbox").checked = true;
                                } else{
                                    task.done = false
                                    var update = getTaskList
                                    console.log(getTaskList)
                                    return setTaskList(update)
                                }
                            }}>
                        </input>
                        <label>{task.name}</label>
                        <button onClick={delTask}>X</button>               
                    </div>

                    )
            })}
        </div>
    )
}
