import { useTaskList } from "../AppContext/AppProvider";
import { Modal, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useState, useRef } from "react";
import liststyle from "./liststyle.css";

export const ListTask = () => {
  const { getTaskList, setTaskList } = useTaskList();

  return (
    <div>
      {getTaskList.map((task) => {

        function DelTask() {
          const [modalEdit, setmodalEdit] = useState(false);
          const closeModalEdit = () => setmodalEdit(false);
          const openModalEdit = () => setmodalEdit(true);
          const updateText = useRef();

          function checkDel(){

          }


         
          /// Exibe alerta de sucesso
          return (
            <div>
              <button className="editbutton" onClick={openModalEdit}>
                ✏️
              </button>

              <Modal show={modalEdit} onHide={closeModalEdit} contentClassName>
                <div className="editH1">
                  <h1>Editar Tarefa</h1>
                </div>

                <hr style={{ "margin-top": "-10px" }}></hr>
                <form onSubmit={checkUpdate} className="inputDiv">
                  <input
                    placeholder={task.name}
                    ref={updateText}
                    className="editInput"
                    maxLength={45}
                  ></input>
                </form>
                <hr></hr>

                <div className="buttonsDiv">
                  <Button
                    variant="secondary"
                    className="editButtons"
                    onClick={closeModalEdit}
                  >
                    Fechar
                  </Button>
                  <Button
                    variant="primary"
                    className="editButtons"
                    onClick={checkUpdate}
                  >
                    Salvar
                  </Button>
                </div>
              </Modal>
            </div>
          );
        }

        function checkBox() {
          if (task.done) {
            task.done = false;
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList;
            });
            setTaskList(boxUpdate);
            console.log("O status da tarefa foi atulizado para 'Em andamento'");
          } else {
            task.done = true;
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList;
            });
            setTaskList(boxUpdate);
            console.log("O status da tarefa foi atulizado para 'Completo'");
          }
        }

        function UpdateTask() {
          const [modalEdit, setmodalEdit] = useState(false);
          const closeModalEdit = () => setmodalEdit(false);
          const openModalEdit = () => setmodalEdit(true);
          const updateText = useRef();

          function checkUpdate(e) {
            e.preventDefault();
            /// Remove os "espaço" iniciais do input
            var edittext = updateText.current.value;
            while (edittext.charAt(0) === " ") {
              edittext = edittext.substring(1);
              console.log(edittext);
            }
            /// Remove os "espaço" iniciais do input
            var checkText = edittext.toString().indexOf(" ");
            
            if (checkText !== 0 && edittext !== "" && edittext !== null) {
              task.name = edittext;
              /// Atualiza o "nome" da tarefa e a atualiza o array
              const boxUpdate = getTaskList.filter(() => {
                return getTaskList;
              });
              /// Atualiza o "nome" da tarefa e a atualiza o array
              /// Salva o novo "nome da tarefa"
              setTaskList(boxUpdate);
              /// Salva o novo "nome da tarefa"
              openModalEdit(false);
              /// Exibe alerta de sucesso
              toast.success("Tarefa atualizada com sucesso!", {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                theme: "colored",
                progress: undefined,
              });
              /// Exibe alerta de sucesso
              /// Limpa o input
              updateText.current.value = "";
              /// Limpa o input
            } else {
              /// Exibe alerta de erro
              toast.error("Não é possivel criar uma tarefa vazia!", {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                theme: "colored",
                progress: undefined,
              });
              /// Exibe alerta de erro
              /// Limpa o input
              updateText.current.value = "";
              /// Limpa o input
            }
          }
          return (
            <div>
              <button className="editbutton" onClick={openModalEdit}>
                ✏️
              </button>

              <Modal show={modalEdit} onHide={closeModalEdit} contentClassName>
                <div className="editH1">
                  <h1>Editar Tarefa</h1>
                </div>

                <hr style={{ "margin-top": "-10px" }}></hr>
                <form onSubmit={checkUpdate} className="inputDiv">
                  <input
                    placeholder={task.name}
                    ref={updateText}
                    className="editInput"
                    maxLength={45}
                  ></input>
                </form>
                <hr></hr>

                <div className="buttonsDiv">
                  <Button
                    variant="secondary"
                    className="editButtons"
                    onClick={closeModalEdit}
                  >
                    Fechar
                  </Button>
                  <Button
                    variant="primary"
                    className="editButtons"
                    onClick={checkUpdate}
                  >
                    Salvar
                  </Button>
                </div>
              </Modal>
            </div>
          );
        }
        return (
          <div className="tasklist" key={task.id}>
            <input
              className="checkbox"
              type="checkbox"
              checked={task.done}
              onChange={checkBox}
            ></input>
            <label className="tasktext">{task.name}</label>
            <UpdateTask></UpdateTask>
            <button className="deletebutton" onClick={delTask}>
              ❌
            </button>
          </div>
        );
      })}
      <ToastContainer />
    </div>
  );
};
