import { useState, useEffect, useContext }  from 'react'
import useText from '../AppContext/AppContext'
import { AppProvider } from '../AppContext/AppProvider'

export const ListTask = ({getLocalStorage}) =>{

    const { getText, setText} = useContext(useText)

    return(
        <div className="taskList">
            <p>{getText}</p>
            {/* Renderizar a lista de tarefas */}
                {JSON.parse(localStorage.getItem('task')).map((individualTask,index) => (
                    <div className='renderTask' key={individualTask.id}>
                        <input type='checkbox'/>
                        <span>{individualTask.name}</span>
                    </div>
                ))}
        </div>
    )
}
