import { useTaskList } from "../AppContext/AppProvider"
import { Modal, Button } from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.min.css"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useState, useRef } from "react"
import liststyle from "./liststyle.css"

export const ListTask = () => {
  const { getTaskList, setTaskList } = useTaskList()

  return (
    <div>
      {getTaskList.map((task) => {
        function delTask() {
          var id = task.id
          const fill = getTaskList.filter((task) => {
            return task.id !== id
          })
          setTaskList(fill)
          console.log("A tarefa '" + task.name + "' foi excluida")
        }

        function checkBox() {
          if (task.done) {
            task.done = false
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
            console.log("O status da tarefa foi atulizado para 'Em andamento'")
          } else {
            task.done = true
            const boxUpdate = getTaskList.filter(() => {
              return getTaskList
            })
            setTaskList(boxUpdate)
            console.log("O status da tarefa foi atulizado para 'Completo'")
          }
        }

        function UpdateTask() {
          const [showModal, setShowModal] = useState(false)
          const handleClose = () => setShowModal(false)
          const handleShow = () => setShowModal(true)
          const updateText = useRef()

          function checkUpdate(e) {
            e.preventDefault()
            var checkText = updateText.current.value.toString().indexOf(" ")

            var name = target.current.value
    while(name.charAt(0) === ' ')
    {
      name = name.substring(1);
      console.log(name)
    }
           




            if (
              updateText.current.value !== "" &&
              checkText !== 0 &&
              updateText.current.value !== null
            ) {
             
              task.name = updateText.current.value
              const boxUpdate = getTaskList.filter(() => {
                return getTaskList
              })
              setTaskList(boxUpdate)
              setShowModal(false)
              console.log("Tarefa atualizada para: " + task.name)
            } else {
              toast.error('Não é possivel criar uma tarefa vazia!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                theme: "colored",
                progress: undefined,
                });
                
                updateText.current.value = ""
            }
          }

          return (
            <div>
              <button className="editbutton" onClick={handleShow}>
                ✏️
              </button>

              <Modal show={showModal} onHide={handleClose} contentClassName>
                <div className="editH1">
                  <h1>Editar Tarefa</h1>
                  
                </div>

                <hr style={{"margin-top": "-10px"}}></hr>
                <form onSubmit={checkUpdate} className="inputDiv">
                    <input
                      placeholder={task.name}
                      ref={updateText}
                      className="editInput"
                      maxLength={45}
                    ></input>
                  </form>
                  <hr></hr>

                <div className="buttonsDiv">
                  <Button variant="secondary"className="editButtons" onClick={handleClose}>
                    Fechar
                  </Button>
                  <Button variant="primary" className="editButtons" onClick={checkUpdate}>
                    Salvar
                  </Button>
                </div>
              </Modal>
            </div>
          )
        }
        return (
          <div className="tasklist" key={task.id}>
            <input
              className="checkbox"
              type="checkbox"
              checked={task.done}
              onChange={checkBox}
            ></input>
            <label className="tasktext">{task.name}</label>
            <UpdateTask></UpdateTask>
            <button className="deletebutton" onClick={delTask}>
              ❌
            </button>
          </div>
        )
      })}
    </div>
  )
}
