        return JSON.parse(data)
    }else{
        return []
    }
}

useEffect(()=>{
    localStorage.setItem('task', JSON.stringify(getTaskList))
},[getLocalStorage])

export const TaskApp = () => { 

        return(
            <div>
                <h1>Lista de Tarefas</h1>
                <hr></hr>
                <AddTask getLocalStorage={getLocalStorage} />
                <ListTask getLocalStorage={getLocalStorage} />
            </div>
        )
    
}
