import { useEffect, useState, useRef } from "react";
import { useText, useTaskList } from "../AppContext/AppProvider";
import { Overlay, Modal, Alert } from "react-bootstrap";
import addstyle from "./addstyle.css";

export const AddTask = () => {
  const { getText, setText } = useText();
  const { getTaskList, setTaskList } = useTaskList();
  const [show, setShow] = useState(false);
  const target = useRef("");
  const displayTarget = useRef(null);

  /// Função de envio de formulario

  function UpdateTask() {
    const [showModal, setShowModal] = useState(false)
    const handleClose = () => setShowModal(false)
    const handleShow = () => setShowModal(true)
    const [show, setShow] = useState(false)

    function checkUpdate(e) {
      var checkText = updateText.current.value.toString().indexOf(" ")

      if (
        updateText.current.value !== "" &&
        checkText !== 0 &&
        updateText.current.value !== null
      ) {
        e.preventDefault()
        task.name = updateText.current.value
        const boxUpdate = getTaskList.filter(() => {
          return getTaskList
        })
        setTaskList(boxUpdate)
        setShowModal(false)
        console.log("Tarefa atualizada para: " + task.name)
      } else {
        e.preventDefault()
        setShow(true)
        console.log("Erro ao atualizar a tarefa!")
      }
    }

    return (
      <div>
        <button className="editbutton" onClick={handleShow}>
          ✏️
        </button>

        <Modal show={showModal} onHide={handleClose} contentClassName>
          <div className="editH1">
            <h1>Editar Tarefa</h1>
            
          </div>

          <hr style={{"margin-top": "-10px"}}></hr>
          <form onSubmit={checkUpdate} className="inputDiv">
              <input
                placeholder={task.name}
                ref={updateText}
                className="editInput"
                maxLength={50}
              ></input>
            </form>
            <hr></hr>

          <div className="buttonsDiv">
            <Button variant="secondary"className="editButtons" onClick={handleClose}>
              Fechar
            </Button>
            <Button variant="primary" className="editButtons" onClick={checkUpdate}>
              Salvar
            </Button>
          </div>
          
          <Alert
            show={show}
            variant="danger"
            onClose={() => setShow(false)}
            dismissible
            style={{"margin-bottom": "0px"}}
          >
          <Alert.Heading>Erro ao atualizar a tarefa!</Alert.Heading>
            <p >A tarefa não pode estar vazia ou começar com um espaço</p>
          </Alert>
        </Modal>
      </div>
    )
  }

  function TaskAlert() {
    return (
        <Overlay target={displayTarget.current} show={show} placement="left">
          {({ placement, arrowProps, show: _show, popper, ...props }) => (
            <div
              {...props}
              style={{
                ...props.style
              }}
            >
              <Alert
                show={show}
                variant="dark"
                style={{padding: "20px"}}
              ><div className="alertHeader">
                <h5 className="alertH5">Erro ao criar a tarefa!</h5>
                <button className="alertButton" onClick={() => setShow(false)}>❌</button>
              </div>
                
                <hr></hr>
                <p className="alertP">A tarefa não pode estar vazia</p>
                <p className="alertP">ou começar com um espaço</p>
              </Alert>
            </div>
          )}
        </Overlay>
    );
  }

  const createTask = (e) => {
    e.preventDefault();

    var checkText = target.current.value.toString().indexOf(" ");

    if (
      checkText !== 0 &&
      target.current.value !== "" &&
      target.current.value !== null
    ) {
      /// Criar objeto "tarefa"
      let newtask = {
        id: new Date().getTime().toString(),
        name: target.current.value,
        done: false,
      };
      /// Salvar objeto "tarefa" no array
      setTaskList([...getTaskList, newtask]);
      setText("");
      setShow(false);
      target.current.value = "";
    } else {
      setShow(true);
    }
  };

  /// Salvar no localstorage
  useEffect(() => {
    localStorage.setItem("task", JSON.stringify(getTaskList));
  }, [getTaskList]);

  return (
    /// Inicio do formulario
    <div ref={displayTarget}>
      <form autoComplete="off" onSubmit={createTask} className="addtask">
        <input
          type="text"
          placeholder="Adicionar Tarefa"
          required
          ref={target}
          className="addtext"
          maxLength={50}
        />
        <button type="submit" className="addbutton" onClick={createTask}>
          ➕
        </button>
        <TaskAlert></TaskAlert>
      </form>
      
    </div>

    /// Fim do formulario
  );
};
